import {FETCH_POSTS, NEW_POST} from '../actions/types.js';

const initialState = {
    items : [],
    item : {}
};

export default function (state = initialState, action){
    console.log(action)
    switch (action.type){
        case FETCH_POSTS:
            return {
                ...state,  //destructioning ES6
                items: action.payload
            };
        case NEW_POST:
            return {
                ...state,
                items: [...state.items, action.payload ]
            };
        default:
            return state;
    }
}
