import React, {Component} from "react";
import { connect } from 'react-redux';
import Styles from "./Styles.css";
import Previous from "./Previous";
import Panel from "./Panel";
import Next from "./Next";
import { Data } from '../data/dataPicker.js';
import { fetchData } from '../media-store/actions/actions.js';

class Slider extends Component {
    constructor(props) {
        super(props);
        this.state = {
            name: 'Slider',
            next: 'NEXT',
            previous: 'PREVIOUS',
            imageUrl: '',
            placeholder: '500 X 500 photo',
            dataIndex : 0,
        }
    }

    componentDidMount() {
       this.props.dispatch(fetchData(Data));
       console.log(Data);
       this.setState({
           imageUrl : Data[this.state.dataIndex]
       })
    }

    LoadPreviousImage() {

    }

    LoadNextImage() {

    }

    render() {
        return (
            <div className={Styles.container}>
                <div className={Styles.Slider}>

                    <Previous
                        previous={() => this.LoadPreviousImage}
                        title={this.state.previous}
                    />

                    <Panel
                        image={this.state.imageUrl}
                        placeholder={this.state.placeholder}
                    />

                    <Next
                        next={() => this.LoadNextImage}
                        title={this.state.next}
                    />

                </div>
            </div>

        )
    }
}
const mapStateToProps = (state) => {
    return {
        mediaStatue : state,
    }
};
export default connect(mapStateToProps)(Slider);
