    previousIndexer : 1,
};

const mediaReducer = (state = initState, action) => {
    switch (action.type) {
        case 'NEXT_SLIDE':
            initState.next = initState.photos[initState.nextIndexer];
            initState.nextIndexer = initState.nextIndexer +1;
            console.log(initState.photos["slide" + initState.nextIndexer])
            return state;
        case 'PREVIOUS_SLIDE':
            initState.next = initState.photos[initState.previousIndexer];
            initState.nextIndexer = initState.nextIndexer -1;
            return state;
        case 'FETCH_DATA':
                initState.photos = action.payload;
            return state;
        case 'READ_DATA':

            return state;
        default:
            return state;

    }
};
export default mediaReducer;
