import React, {Component} from "react";
import Styles from "./Styles.css";
import { connect } from "react-redux";
import {readData} from "../media-store/actions/actions";
import  { Data }  from '../data/dataPicker.js';

class Grid extends Component {
    componentDidMount(){
      this.props.dispatch(readData(Data))
    }
    render() {
        return (
            <div className={Styles.PreviousNextContainer}>
                <p>next photo : {this.props.mediaData.next} </p>
                <p>previous photo : {this.props.mediaData.previous}</p>
                <p>current photo : {this.props.mediaData.photos}</p>
            </div>
        )
    }
}
const mapStateToProps = (state) => {
    console.log(state)
    return {
        mediaData: state,
    }
};
export default connect(mapStateToProps)(Grid);
