const initState = {
    photos: [],
    next: '',
    previous: '',
    nextIndexer: 1,
    previousIndexer: 1,
};

const mediaReducer = (state = initState, action) => {
    switch (action.type) {
        case 'NEXT_SLIDE':
            initState.next = initState.nextIndexer + 1;
            initState.next = initState.photos["slide" + initState.nextIndexer];
            return state;
        case 'PREVIOUS_SLIDE':
            initState.previous = initState.nextIndexer - 1;
            initState.previous = initState.photos["slide" + initState.nextIndexer];
            return state;
        case 'FETCH_DATA':
            initState.photos = action.payload;
            initState.previous = initState.photos["slide" + initState.nextIndexer];
            initState.next = initState.photos["slide" + (initState.nextIndexer + 1)];
            return state;
        case 'READ_DATA':

            return state;
        default:
            return state;

    }
};
export default mediaReducer;
